    _FilesController["default"].getIndex(req, res);
  });

  // should set isPublic to true on the file document based on the ID
  router.put('/files/:id/publish', function (req, res) {
    _FilesController["default"].putPublish(req, res);
  });

  // should set isPublic to false on the file document based on the ID
  router.put('/files/:id/unpublish', function (req, res) {
    _FilesController["default"].putUnpublish(req, res);
  });

  // should return the content of the file document based on the ID
  router.get('/files/:id/data', function (req, res) {
    _FilesController["default"].getFile(req, res);
  });
